import { inputAdornmentClasses } from "@mui/material";
import "./WeightForm.css";
import { useState } from "react";
import { AiOutlinePlus, AiOutlineMinus } from "react-icons/ai";

export default function WeightForm() {
  const [weight, setWeight] = useState(0);

  const handleInputChange = (event) => {
    setWeight(event.target.value);
  };

  return (
    <div id="weight_form">
      <h3 id="weight_form_title"> Enter today's weight</h3>
      <form>
        <div>
          <table>
            <tr>
              <td onClick={() => setWeight(weight - 1)}><AiOutlineMinus/></td>
              <td>
                <input value={weight} onChange={handleInputChange}></input>
              </td>
              <td onClick={() => setWeight(weight + 1)}> <AiOutlinePlus/></td>
            </tr>
          </table>
        </div>
      </form>
    </div>
  );
}
